    // The calculaction method
    private void jButtonEqualMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButtonEqualMouseClicked
        // Using the JavaScript engine for the eval() method to calculate
        // a string
        ScriptEngineManager s = new ScriptEngineManager();
        ScriptEngine engine = s.getEngineByName("JavaScript");
        try {
            String result = ((engine.eval(calcTextField.getText()).toString()));
            calcDisplay.setText(result);
        } catch (javax.script.ScriptException e) {
            e.printStackTrace();
            calcDisplay.setText("ERROR!SOMETHING WENT WRONG!");
        }
    }//GEN-LAST:event_jButtonEqualMouseClicked

